document.getElementById('addstudent').onsubmit = function(e) {
    debugger
    e.preventDefault(); // Prevent the default form submission
        // Get form data
    var formData = new FormData(this);

    // Get individual form field values
    var first_name = formData.get('firstname');
    var last_name = formData.get('lastname');
    var gender = formData.get('gender');
    var img = formData.get('profile_img');

    frappe.call({
        method: 'sage.sage.student_hooks.create_student',
        args: {
            firstname:first_name,
            lastname:last_name,
            gender:gender,
            profile_img:img
        },
        callback: function(response) {
        if (response.message) {
          alert("success")

        } else {

        }
      }
    });
};

